IMG(index);            changeSrc(index);            if(index < canMove)                slideContent(index);        }        $('.all-image-simple img').click(function(){            index = $(this).parent().index() -1;            changecssIMG(index);            changeSrc(index);            if(index <= canMove)                slideContent(index);        });        $('#leftrow-simple img').click(function(){            slideLeft();        });        $('#rightrow-simple img').click(function(){            slideRight();        });    });</script><div id = "image-bg">    <!-- nho tinh width va heigt cua hinh bo vao style -->    <?php        $bg_img = field_get_items("node",$items,"field_background_image");        if($bg_img){            $bg_url = file_create_url($bg_img[0]['uri']);            $img = getimagesize($bg_url);            $w = "width:".$img[0]."px".";";            $h = "height:".$img[1]."px".";";            print '<img style='.$w.$h.' src='.$bg_url.'>';        }else{    ?>        <img class="image-bg" style="width: 1331px; height: 657px;" src="<?php print base_path(). drupal_get_path('theme', 'daokimcuong').'/images/hinhnen.png' ?>">    <?php } ?></div><div id="wrap-simple-content">   <div class="inside-wrap">        <div class="header-simple"> <!-- phan header - breadcrums -->            <div style="height: 3px; width: 100%;"></div> <!-- div trong -->            <div class="bread-simple" >                <?php if ($breadcrumbs){ print $breadcrumbs;}?>            </div>        </div>       <div class="content-simple"><!-- phan slide show  -->           <div class="content-img-simple">               <?php                    $img = field_get_items("node",$items,"field_image");                    $img_url = file_create_url($img[0]['uri']);                    if($img_url){                        print '<img src='.$img_url.'>';                    }               ?>           </div>           <div style="width: 100%;;">               <div id="leftrow-simple">                   <img src="<?php print base_path(). drupal_get_path('theme', 'daokimcuong').'/images/left-row.png' ?>">               </div>               <div class="list-image-simple">                   <div class="inside-list-image" style=" width: 2000px; "> <!-- se thay doi width -->                       <div style="height: 18px; width: 100%; "></div> <!-- div trong -->                       <?php                            $imgs = field_get_items("node",$items,"field_image");                            $count = count($imgs);                            for($i = 0; $i < $count; $i++){                                $img_url = $imgs[$i]['uri'];                                $img_thumb_url =  image_style_url('thumbnail', $img_url);                                $img_full_url = file_create_url($imgs[$i]['uri']);                                if($i == 0){                                    print "<div class='all-image-simple'>";                                    print '<img class="img-simp-selected" alt='.$img_full_url.' src='.$img_thumb_url.'>';                                    print "</div>";                                }else{                                    print "<div class='all-image-simple'>";                                    print '<img alt='.$img_full_url.' src='.$img_thumb_url.'>';                                    print "</div>";                                }                            }                       ?>                   </div>               </div>               <div id="rightrow-simple">                   <img src="<?php print base_path(). drupal_get_path('theme', 'daokimcuong').'/images/right-row.png' ?>">               </div>           </div>       </div>       <div class="footer-simple"> <!-- phan  footer - content -->            <div class="header-info">                <span>                   <?php print $items->title; ?>                </span>            </div>           <div class="content-info">                <span>                    <?php                        $body = field_get_items("node",$items,"body");                        print $body[0]['value'];                    ?>                </span>           </div>       </div>   </div></div>