avigator.platform == "iPad")    {        $('body').css('zoom','0.8');    }    $('#navigation ul li > a').each(function(index, domEle){        if ( $(this).is( ".menuparent" ) )        {            $(this).attr('href','javascript:void(0);');        }       });    $('#menu-bottom ul li > a').each(function(index, domEle){        if ( $(this).is( ".menuparent" ) )        {            $(this).attr('href','javascript:void(0);');        }    });</script><script type="text/javascript">    function image_resize(id) {        $(id).each(function () {            /* Max width for the image */            var maxWidth = $(window).width();            /* Max hieght for the image */            var maxHeight = $(window).height();            if(navigator.platform == "iPad")            {                 maxHeight = 768;                 maxWidth = 1024;            }            /*Used for aspect ratio*/            var ratio = 0;            /*Current image width*/            var width = $(this).width();            /*Current image height */            var height = $(this).height();            if (maxWidth > maxHeight) {                if (width > height) {                    var fRatio = width/height;                    $(this).css({                        width : maxWidth,                        height : Math.round(maxWidth * (1/fRatio))                    });                    var newHeight = Math.round(maxWidth * (1/fRatio));                    if(newHeight < maxHeight) {                        var fRatio = height/width;                        $(this).css({                            width : Math.round(maxHeight* (1/fRatio)),                            height : maxHeight                        });                    }                } else {                    var fRatio = height/width;                    $(this).css({                        width : Math.round(maxHeight * (1/fRatio)),                        height : maxHeight                    });                }            } else {                var fRatio = height/width;                $(this).css({                    width : Math.round(maxHeight * (1/fRatio)),                    height : maxHeight                });            }            var center = true;            if (typeof(center) == 'undefined' || center) {                if (width > maxWidth) {                    var left = (width - maxWidth) / 2;                    $(this).css({                        top  : 0,                        left : -left                    });                }                if (height > maxHeight) {                    var this_height = (height - maxHeight) / 2;                    $(this).css({                        left : 0,                        top : -this_height                    });                }            }        });    }</script><script type="text/javascript">    var id = "#slide1_container img";    $(document).ready(function(){        image_resize(id);    });    $(window).resize(function () {        image_resize(id);    });    $(window).load(function () {        image_resize(id);    });</script>